import { createSlice } from "@reduxjs/toolkit";

const rideSlice = createSlice({
  name: "ride",
  initialState: {
    searchedRide: [],
    requestedRide: [],
  },
  reducers: {
    searchedRide: (ride, action) => {
      ride.searchedRide = action.payload;
    },

    requestedRide: (ride, action) => {
      ride.requestedRide = action.data;
    },
  },
});

export const { searchedRide, requestedRide } = rideSlice.actions;

export default rideSlice.reducer;
