import React from "react";
import { connect } from "react-redux";

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h4>Centered Modal</h4>
        <p>
          Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
          dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
          consectetur ac, vestibulum at eros.
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

const RideInfo = () => {
  // const selectedRideIndex = this.props.location.state.index;
  // const ride = this.props.ride.searchedRide[selectedRideIndex];
  const ride = "";
  console.log(ride);
  const [modalShow, setModalShow] = React.useState(false);
  return (
    <div>
      <div className="row justify-content-md-center my-3">
        <div
          className="card col-md-8 col-sm-10 shadow"
          style={{ height: "500px" }}
        >
          <div className="card-title my-2 mx-3 text-primary row">
            <div className="col">
              Departure: {ride.date} at {ride.time}
            </div>
            <div className="col">Phone Number : 5196140966</div>
            <button
              variant="primary"
              onClick={() => setModalShow(true)}
              className="btn btn-primary col"
            >
              Send Email
            </button>
          </div>

          <div className="card-body row">
            <div className="col">
              <p>{ride.userName}</p>
              <p>4 Rating</p>
            </div>
            <div className="col">
              <p className="text-secondary">FROM</p>
              <strong>{ride.from}</strong>
            </div>
            <div className="col">
              <strong style={{ fontSize: "50px" }}>&#8674;</strong>
              <p></p>
            </div>
            <div className="col">
              <p className="text-secondary">TO</p>
              <strong>{ride.to}</strong>
            </div>
            <div className="col ">
              <h2 className="text-success">${ride.price}</h2>
            </div>
          </div>
          <div className=" text-center text-secondary">
            <p> Stops: {ride.stops}</p>
          </div>
          <div>Vehicle details</div>
          <div>Name: Mazda Color: White Seats: 5</div>
        </div>
      </div>

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </div>
  );
};

const mapStateToProps = state => ({
  ride: state.ride,
});

export default connect(mapStateToProps)(RideInfo);
