import React from "react";
import Form from "../../common/form";
import { connect } from "react-redux";
import Joi from "@hapi/joi";

class UserInfo extends Form {
  state = {
    data: {
      name: this.props.auth,
      email: "",
      phoneNumber: "",
    },
    errors: {},
  };

  schema = {
    name: Joi.string().max(255).label("Name"),
    email: Joi.string().max(255).label("Email"),
    phoneNumber: Joi.number().label("Mobile Number"),
  };

  render() {
    return (
      <div>
        {this.renderInput("name", "Full Name")}
        {this.renderInput("email", "Email")}
        {this.renderInput("phoneNumber", "Mobile Phone")}
      </div>
    );
  }
}

const mapStateToProps = function (auth) {
  return {
    auth: auth.user,
  };
};

export default connect(mapStateToProps)(UserInfo);
