import React from "react";
import Form from "../common/form";
import rideService from "../services/rideService";
import Joi from "joi-browser";
import ShowRides from "./showRides";

class SearchRide extends Form {
  state = {
    data: {
      from: "",
      to: "",
    },
    searchData: "",
    errors: {},
  };

  schema = {
    from: Joi.string().min(3).max(255).required().label("From"),
    to: Joi.string().min(3).max(255).required().label("To"),
  };

  async doSubmit() {
    const searchData = this.state.data;
    try {
      const response = await rideService.searchRides(searchData);
      this.setState({ searchData: response.data });
    } catch (ex) {
      const errors = { ...this.state.errors };
      errors.from = ex.response;
      this.setState({ errors });
    }
  }

  render() {
    const rideList = this.state.searchData;
    //console.log(rideList);
    let result;
    if (rideList) {
      if (rideList.length === 0) {
        message = <div>There is no rides</div>;
        return message;
      } else {
        rideList.map(ride => {
          message = <ShowRides key={ride._id} ride={ride} />;
          return message;
        });
      }
    } else {
      return (message = <p></p>);
    }

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("from", "From")}
          {this.renderInput("to", "To")}
          {this.renderButton("Search")}
        </form>
        {rideList ? (
          rideList.map(ride => {
            return <ShowRides key={ride._id} ride={ride} />;
          })
        ) : rideList.length === 0 ? (
          <p>There is no ride at this point </p>
        ) : (
          <p></p>
        )}
      </div>
    );
  }
}

export default SearchRide;
