import { createSlice } from "@reduxjs/toolkit";

const rideSlice = createSlice({
  name: "ride",
  initialState: {
    searchedRides: [],
    requestedRides: null,
  },
  reducers: {
    searchedRide: (ride, action) => {
      ride.searchedRides.push(action.payload.message);
    },

    requestedRide: (state, action) => {
      state.requestedRides = true;
    },
  },
});

export const { searchedRide, requestedRide } = rideSlice.actions;

export default rideSlice.reducer;
