import React from "react";
import Form from "../../common/form";
import Joi from "joi-browser";
import jwtDecode from "jwt-decode";

class ResetPassword extends Form {
  state = {
    data: { password: "", confirmPassword: "" },
    errors: {},
  };
  schema = {
    password: Joi.string().min(5).max(255).required().label("Password"),
    confirmPassword: Joi.string().min(5).max(255).required().label("Password"),
  };

  async doSubmit() {
    const pass = this.state.data;
    if (pass.password !== pass.confirmPassword) {
      const errors = { ...this.state.errors };
      errors = "Password doesn't match";
      return this.setState(errors);
    }

    console.log(pass);
  }

  render() {
    let user = jwtDecode(this.props.match.params.token);
    return (
      <div className="row">
        <div>
          <form onSubmit={this.handleSubmit}>
            <legend>Setup New Password</legend>
            {this.renderInput("password", "New Password")}
            {this.renderInput("confirmPassword", "Confirm Password")}
            {this.renderButton("Reset Password")}
          </form>
        </div>
      </div>
    );
  }
}

export default ResetPassword;
