import { createSlice } from "@reduxjs/toolkit";

const rideSlice = createSlice({
  name: "ride",
  initialState: {
    searchedRides: [],
    requestedRides: null,
  },
  reducers: {
    searchedRide(ride = initialState, action) {
      return {
        ...ride,
        searchedRides: action.payload.message,
      };
    },

    requestedRide(state, action) {
      state.requestedRides = true;
    },
  },
});

export const { searchedRide, requestedRide } = rideSlice.actions;

export default rideSlice.reducer;
